import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { DocumentService, DocumentDto, DocumentIngestRequest } from '../../services/document.service';

@Component({
  selector: 'app-document-management',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './document-management.component.html',
  styleUrls: ['./document-management.component.css']
})
export class DocumentManagementComponent implements OnInit {
  documents: DocumentDto[] = [];
  isLoading = false;
  error: string | null = null;

  // Upload form
  showUploadForm = false;
  uploadTitle = '';
  uploadType: 'PDF' | 'DOCX' | 'TXT' | 'URL' = 'PDF';
  selectedFile: File | null = null;
  uploadUrl = '';
  isUploading = false;

  constructor(private documentService: DocumentService) {}

  ngOnInit() {
    this.loadDocuments();

    // Refresh every 5 seconds to update processing status
    setInterval(() => {
      this.loadDocuments();
    }, 5000);
  }

  loadDocuments() {
    this.documentService.getAllDocuments().subscribe({
      next: (docs) => {
        this.documents = docs;
      },
      error: (err) => {
        console.error('Failed to load documents:', err);
        this.error = 'Failed to load documents';
      }
    });
  }

  toggleUploadForm() {
    this.showUploadForm = !this.showUploadForm;
    if (this.showUploadForm) {
      this.resetUploadForm();
    }
  }

  resetUploadForm() {
    this.uploadTitle = '';
    this.uploadType = 'PDF';
    this.selectedFile = null;
    this.uploadUrl = '';
    this.error = null;
  }

  onFileSelected(event: any) {
    const file = event.target.files[0];
    if (file) {
      this.selectedFile = file;

      // Auto-fill title from filename
      if (!this.uploadTitle) {
        this.uploadTitle = file.name.replace(/\.[^/.]+$/, '');
      }
    }
  }

  uploadDocument() {
    if (!this.uploadTitle) {
      this.error = 'Please enter a title';
      return;
    }

    this.isUploading = true;
    this.error = null;

    if (this.uploadType === 'URL') {
      // Ingest from URL
      if (!this.uploadUrl) {
        this.error = 'Please enter a URL';
        this.isUploading = false;
        return;
      }

      const request: DocumentIngestRequest = {
        title: this.uploadTitle,
        sourceUrl: this.uploadUrl,
        documentType: 'URL'
      };

      this.documentService.ingestFromUrl(request).subscribe({
        next: (doc) => {
          console.log('URL ingestion started:', doc);
          this.documents.unshift(doc);
          this.toggleUploadForm();
          this.isUploading = false;
        },
        error: (err) => {
          console.error('Failed to ingest URL:', err);
          this.error = 'Failed to ingest URL: ' + (err.error?.message || err.message);
          this.isUploading = false;
        }
      });
    } else {
      // Upload file
      if (!this.selectedFile) {
        this.error = 'Please select a file';
        this.isUploading = false;
        return;
      }

      this.documentService.uploadDocument(this.selectedFile, this.uploadTitle, this.uploadType).subscribe({
        next: (doc) => {
          console.log('Document uploaded:', doc);
          this.documents.unshift(doc);
          this.toggleUploadForm();
          this.isUploading = false;
        },
        error: (err) => {
          console.error('Failed to upload document:', err);
          this.error = 'Failed to upload document: ' + (err.error?.message || err.message);
          this.isUploading = false;
        }
      });
    }
  }

  deleteDocument(id: number) {
    if (!confirm('Are you sure you want to delete this document?')) {
      return;
    }

    this.documentService.deleteDocument(id).subscribe({
      next: () => {
        console.log('Document deleted:', id);
        this.documents = this.documents.filter(d => d.id !== id);
      },
      error: (err) => {
        console.error('Failed to delete document:', err);
        this.error = 'Failed to delete document';
      }
    });
  }

  getStatusClass(status: string): string {
    switch (status) {
      case 'COMPLETED': return 'status-completed';
      case 'PROCESSING': return 'status-processing';
      case 'PENDING': return 'status-pending';
      case 'FAILED': return 'status-failed';
      default: return '';
    }
  }

  getStatusIcon(status: string): string {
    switch (status) {
      case 'COMPLETED': return '‚úì';
      case 'PROCESSING': return '‚ü≥';
      case 'PENDING': return '‚ãØ';
      case 'FAILED': return '‚úó';
      default: return '';
    }
  }

  formatDate(dateString: string): string {
    const date = new Date(dateString);
    return date.toLocaleString();
  }
}
<div class="document-management">
  <div class="header">
    <h2>Document Management</h2>
    <button class="btn btn-primary" (click)="toggleUploadForm()">
      {{ showUploadForm ? 'Cancel' : '+ Add Document' }}
    </button>
  </div>

  <div *ngIf="error" class="alert alert-error">
    {{ error }}
    <button class="alert-close" (click)="error = null">√ó</button>
  </div>

  <!-- Upload Form -->
  <div *ngIf="showUploadForm" class="upload-form">
    <h3>Add New Document</h3>

    <div class="form-group">
      <label>Document Type</label>
      <select [(ngModel)]="uploadType" class="form-control">
        <option value="PDF">PDF</option>
        <option value="DOCX">Word Document (DOCX)</option>
        <option value="TXT">Text File (TXT)</option>
        <option value="URL">Website URL</option>
      </select>
    </div>

    <div class="form-group">
      <label>Title</label>
      <input type="text" [(ngModel)]="uploadTitle" class="form-control" placeholder="Enter document title">
    </div>

    <div *ngIf="uploadType === 'URL'" class="form-group">
      <label>URL</label>
      <input type="url" [(ngModel)]="uploadUrl" class="form-control" placeholder="https://example.com/page">
    </div>

    <div *ngIf="uploadType !== 'URL'" class="form-group">
      <label>File</label>
      <input type="file" (change)="onFileSelected($event)"
             [accept]="uploadType === 'PDF' ? '.pdf' : uploadType === 'DOCX' ? '.docx' : '.txt'"
             class="form-control">
      <div *ngIf="selectedFile" class="file-info">
        Selected: {{ selectedFile.name }} ({{ (selectedFile.size / 1024).toFixed(1) }} KB)
      </div>
    </div>

    <div class="form-actions">
      <button class="btn btn-primary" (click)="uploadDocument()" [disabled]="isUploading">
        {{ isUploading ? 'Uploading...' : 'Upload' }}
      </button>
      <button class="btn btn-secondary" (click)="toggleUploadForm()" [disabled]="isUploading">
        Cancel
      </button>
    </div>
  </div>

  <!-- Documents List -->
  <div class="documents-list">
    <div *ngIf="documents.length === 0 && !isLoading" class="empty-state">
      <p>No documents yet. Add your first document to get started!</p>
    </div>

    <div *ngFor="let doc of documents" class="document-card">
      <div class="document-header">
        <div class="document-title">
          <span class="document-icon">üìÑ</span>
          <h4>{{ doc.title }}</h4>
        </div>
        <div class="document-actions">
          <button class="btn btn-danger btn-sm" (click)="deleteDocument(doc.id)" title="Delete">
            üóëÔ∏è
          </button>
        </div>
      </div>

      <div class="document-body">
        <div class="document-info">
          <span class="info-label">Type:</span>
          <span class="info-value">{{ doc.documentType }}</span>
        </div>

        <div class="document-info">
          <span class="info-label">Status:</span>
          <span class="info-value" [ngClass]="getStatusClass(doc.status)">
            <span class="status-icon">{{ getStatusIcon(doc.status) }}</span>
            {{ doc.status }}
          </span>
        </div>

        <div *ngIf="doc.chunkCount" class="document-info">
          <span class="info-label">Chunks:</span>
          <span class="info-value">{{ doc.chunkCount }}</span>
        </div>

        <div *ngIf="doc.sourceUrl" class="document-info">
          <span class="info-label">Source:</span>
          <span class="info-value">
            <a [href]="doc.sourceUrl" target="_blank" rel="noopener">{{ doc.sourceUrl }}</a>
          </span>
        </div>

        <div class="document-info">
          <span class="info-label">Created:</span>
          <span class="info-value">{{ formatDate(doc.createdAt) }}</span>
        </div>

        <div *ngIf="doc.errorMessage" class="document-error">
          <strong>Error:</strong> {{ doc.errorMessage }}
        </div>
      </div>
    </div>
  </div>
</div>
