import { Component, Input, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { TenantDto } from '../../services/tenant.service';
import { TenantSettings } from '../../models/widget-config.model';

@Component({
  selector: 'app-widget-customizer',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './widget-customizer.component.html',
  styleUrls: ['./widget-customizer.component.css']
})
export class WidgetCustomizerComponent implements OnInit {
  @Input() tenant!: TenantDto;

  widgetSettings: TenantSettings = {
    widget: {
      position: 'bottom-right',
      primaryColor: '#667eea',
      secondaryColor: '#764ba2',
      showLogo: true,
      enableFeedback: true
    },
    companyName: '',
    welcomeMessage: 'How can we help you?',
    logoUrl: ''
  };

  embedCode: string = '';
  showEmbedCode: boolean = false;

  ngOnInit() {
    this.loadTenantSettings();
    this.generateEmbedCode();
  }

  loadTenantSettings() {
    if (this.tenant.settings) {
      try {
        const settings = typeof this.tenant.settings === 'string'
          ? JSON.parse(this.tenant.settings)
          : this.tenant.settings;

        this.widgetSettings = {
          ...this.widgetSettings,
          ...settings,
          widget: {
            ...this.widgetSettings.widget,
            ...(settings.widget || {})
          }
        };
      } catch (e) {
        console.error('Failed to parse tenant settings:', e);
      }
    }

    // Use tenant name as fallback for companyName
    if (!this.widgetSettings.companyName) {
      this.widgetSettings.companyName = this.tenant.name;
    }
  }

  generateEmbedCode() {
    const config = {
      tenantId: this.tenant.tenantId,
      apiKey: this.tenant.apiKey,
      ...this.widgetSettings,
      ...this.widgetSettings.widget
    };

    this.embedCode = `<!-- ChatQ Assist Widget -->
<script>
  window.chatqConfig = ${JSON.stringify(config, null, 2)};
</script>
<script src="https://your-domain.com/chatq-widget.js"></script>
<!-- End ChatQ Assist Widget -->`;
  }

  copyEmbedCode() {
    navigator.clipboard.writeText(this.embedCode).then(() => {
      alert('Embed code copied to clipboard!');
    }).catch(err => {
      console.error('Failed to copy:', err);
    });
  }

  saveSettings() {
    // This would call the tenant service to update settings
    console.log('Saving widget settings:', this.widgetSettings);
    this.generateEmbedCode();
    alert('Widget settings saved! (Note: Backend integration pending)');
  }

  previewWidget() {
    // Create a simple preview page in a new window with the widget embedded
    const config = {
      ...this.widgetSettings,
      ...this.widgetSettings.widget,
      tenantId: this.tenant.tenantId,
      apiKey: this.tenant.apiKey
    };

    const previewHtml = `
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Widget Preview - ${config.companyName || 'ChatQ Assist'}</title>
  <style>
    body {
      font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
      margin: 0;
      padding: 40px;
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      min-height: 100vh;
    }
    .preview-container {
      background: white;
      border-radius: 16px;
      padding: 48px;
      max-width: 800px;
      margin: 0 auto;
      box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
    }
    h1 { color: #333; margin: 0 0 16px 0; }
    p { color: #666; line-height: 1.6; }
    .info-box {
      background: #f8f9fa;
      border-radius: 8px;
      padding: 16px;
      margin: 24px 0;
      font-size: 14px;
    }
    .info-box strong { color: #333; }
  </style>
</head>
<body>
  <div class="preview-container">
    <h1>üé® Widget Preview</h1>
    <p>This is a live preview of your customized chat widget. Click the chat button in the bottom-right corner to test it!</p>

    <div class="info-box">
      <strong>Configuration:</strong><br>
      Company: ${config.companyName || 'ChatQ Assist'}<br>
      Primary Color: ${config.primaryColor || '#667eea'}<br>
      Position: ${config.position || 'bottom-right'}<br>
      Logo: ${config.logoUrl ? 'Yes' : 'No'}
    </div>

    <p><strong>Note:</strong> This is a preview. The actual widget will be embedded on your customer's website using the embed code provided.</p>
  </div>

  <!-- The actual chat widget would be loaded here -->
  <!-- For now, we'll show a message that it requires the full app -->
  <script>
    alert('Widget Preview: In der finalen Version w√ºrde hier das Chat-Widget mit Ihren Branding-Einstellungen erscheinen.\\n\\nUm das Widget zu testen, verwenden Sie bitte den generierten Embed-Code auf einer Test-Website.');
  </script>
</body>
</html>
    `;

    const blob = new Blob([previewHtml], { type: 'text/html' });
    const url = URL.createObjectURL(blob);
    window.open(url, '_blank');
  }
}
<div class="widget-customizer">
  <div class="customizer-header">
    <h3>üé® Widget Customization</h3>
    <p>Customize the chat widget appearance for {{ tenant.name }}</p>
  </div>

  <div class="customizer-body">
    <!-- Branding Section -->
    <div class="section">
      <h4>Branding</h4>

      <div class="form-group">
        <label for="companyName">Company Name</label>
        <input
          id="companyName"
          type="text"
          [(ngModel)]="widgetSettings.companyName"
          placeholder="Your Company Name"
          class="form-control"
        />
      </div>

      <div class="form-group">
        <label for="welcomeMessage">Welcome Message</label>
        <input
          id="welcomeMessage"
          type="text"
          [(ngModel)]="widgetSettings.welcomeMessage"
          placeholder="How can we help you?"
          class="form-control"
        />
      </div>

      <div class="form-group">
        <label for="logoUrl">Logo URL</label>
        <input
          id="logoUrl"
          type="url"
          [(ngModel)]="widgetSettings.logoUrl"
          placeholder="https://example.com/logo.png"
          class="form-control"
        />
        <small *ngIf="widgetSettings.logoUrl" class="logo-preview">
          <img [src]="widgetSettings.logoUrl" alt="Logo preview" />
        </small>
      </div>
    </div>

    <!-- Colors Section -->
    <div class="section">
      <h4>Colors</h4>

      <div class="color-row">
        <div class="form-group">
          <label for="primaryColor">Primary Color</label>
          <div class="color-input-group">
            <input
              id="primaryColor"
              type="color"
              [(ngModel)]="widgetSettings.widget!.primaryColor"
              class="color-picker"
            />
            <input
              type="text"
              [(ngModel)]="widgetSettings.widget!.primaryColor"
              class="color-text"
              placeholder="#667eea"
            />
          </div>
        </div>

        <div class="form-group">
          <label for="secondaryColor">Secondary Color</label>
          <div class="color-input-group">
            <input
              id="secondaryColor"
              type="color"
              [(ngModel)]="widgetSettings.widget!.secondaryColor"
              class="color-picker"
            />
            <input
              type="text"
              [(ngModel)]="widgetSettings.widget!.secondaryColor"
              class="color-text"
              placeholder="#764ba2"
            />
          </div>
        </div>
      </div>
    </div>

    <!-- Position & Features Section -->
    <div class="section">
      <h4>Position & Features</h4>

      <div class="form-group">
        <label for="position">Widget Position</label>
        <select
          id="position"
          [(ngModel)]="widgetSettings.widget!.position"
          class="form-control"
        >
          <option value="bottom-right">Bottom Right</option>
          <option value="bottom-left">Bottom Left</option>
          <option value="top-right">Top Right</option>
          <option value="top-left">Top Left</option>
        </select>
      </div>

      <div class="checkbox-group">
        <label class="checkbox-label">
          <input
            type="checkbox"
            [(ngModel)]="widgetSettings.widget!.showLogo"
          />
          Show Logo in Header
        </label>

        <label class="checkbox-label">
          <input
            type="checkbox"
            [(ngModel)]="widgetSettings.widget!.enableFeedback"
          />
          Enable User Feedback
        </label>
      </div>
    </div>

    <!-- Preview & Actions -->
    <div class="section actions-section">
      <div class="button-group">
        <button class="btn btn-secondary" (click)="previewWidget()">
          üëÅÔ∏è Preview Widget
        </button>
        <button class="btn btn-primary" (click)="saveSettings()">
          üíæ Save Settings
        </button>
        <button class="btn btn-success" (click)="showEmbedCode = !showEmbedCode">
          üìã {{ showEmbedCode ? 'Hide' : 'Get' }} Embed Code
        </button>
      </div>
    </div>

    <!-- Embed Code Section -->
    <div *ngIf="showEmbedCode" class="section embed-section">
      <h4>üìã Embed Code</h4>
      <p>Copy and paste this code before the closing &lt;/body&gt; tag on your website:</p>

      <div class="embed-code-container">
        <pre>{{ embedCode }}</pre>
        <button class="copy-btn" (click)="copyEmbedCode()">
          üìã Copy Code
        </button>
      </div>

      <div class="embed-info">
        <p><strong>Tenant ID:</strong> {{ tenant.tenantId }}</p>
        <p><strong>API Key:</strong> {{ tenant.apiKey || 'Not generated' }}</p>
      </div>
    </div>
  </div>
</div>
