    this.searchQuery = '';
    this.filterActive = 'all'; // 'all' | 'active' | 'inactive'
    this.isLoading = false;
    this.error = null;
    // Form state
    this.showForm = false;
    this.editingFaq = null;
    this.formData = this.getEmptyFaq();
  }
  ngOnInit() {
    this.loadFaqs();
  }
  switchTab(tab) {
    this.activeTab = tab;
