        this.error = 'Failed to load FAQs. Please try again.';
        this.isLoading = false;
        console.error('Error loading FAQs:', err);
      }
    });
  }
  applyFilters() {
    let filtered = [...this.faqs];
    // Apply search filter
    if (this.searchQuery.trim()) {
      const query = this.searchQuery.toLowerCase();
      filtered = filtered.filter(faq => faq.question.toLowerCase().includes(query) || faq.answer.toLowerCase().includes(query) || faq.tags && faq.tags.some(tag => tag.toLowerCase().includes(query)));
