import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { FaqService, FaqEntry } from '../../services/faq.service';
import { DocumentManagementComponent } from '../document-management/document-management.component';
import { TenantManagementComponent } from '../tenant-management/tenant-management.component';
import { UserManagementComponent } from '../user-management/user-management.component';

@Component({
  selector: 'app-admin-dashboard',
  standalone: true,
  imports: [CommonModule, FormsModule, DocumentManagementComponent, TenantManagementComponent, UserManagementComponent],
  templateUrl: './admin-dashboard.component.html',
  styleUrls: ['./admin-dashboard.component.css']
})
export class AdminDashboardComponent implements OnInit {
  // Tab state
  activeTab: 'faqs' | 'documents' | 'tenants' | 'users' = 'faqs';

  faqs: FaqEntry[] = [];
  filteredFaqs: FaqEntry[] = [];
  searchQuery: string = '';
  filterActive: string = 'all'; // 'all' | 'active' | 'inactive'
  isLoading: boolean = false;
  error: string | null = null;

  // Form state
  showForm: boolean = false;
  editingFaq: FaqEntry | null = null;
  formData: FaqEntry = this.getEmptyFaq();

  constructor(private faqService: FaqService) {}

  ngOnInit(): void {
    this.loadFaqs();
  }

  switchTab(tab: 'faqs' | 'documents' | 'tenants' | 'users'): void {
    this.activeTab = tab;
  }

  loadFaqs(): void {
    this.isLoading = true;
    this.error = null;

    this.faqService.getAllFaqs().subscribe({
      next: (faqs) => {
        this.faqs = faqs.sort((a, b) => {
          // Sort by displayOrder, then by usageCount desc
          if (a.displayOrder !== undefined && b.displayOrder !== undefined) {
            return a.displayOrder - b.displayOrder;
          }
          return (b.usageCount || 0) - (a.usageCount || 0);
        });
        this.applyFilters();
        this.isLoading = false;
      },
      error: (err) => {
        this.error = 'Failed to load FAQs. Please try again.';
        this.isLoading = false;
        console.error('Error loading FAQs:', err);
      }
    });
  }

  applyFilters(): void {
    let filtered = [...this.faqs];

    // Apply search filter
    if (this.searchQuery.trim()) {
      const query = this.searchQuery.toLowerCase();
      filtered = filtered.filter(faq =>
        faq.question.toLowerCase().includes(query) ||
        faq.answer.toLowerCase().includes(query) ||
        (faq.tags && faq.tags.some(tag => tag.toLowerCase().includes(query)))
      );
    }

    // Apply active filter
    if (this.filterActive === 'active') {
      filtered = filtered.filter(faq => faq.isActive === true);
    } else if (this.filterActive === 'inactive') {
      filtered = filtered.filter(faq => faq.isActive === false);
    }

    this.filteredFaqs = filtered;
  }

  onSearchChange(): void {
    this.applyFilters();
  }

  onFilterChange(): void {
    this.applyFilters();
  }

  openCreateForm(): void {
    this.editingFaq = null;
    this.formData = this.getEmptyFaq();
    this.showForm = true;
  }

  openEditForm(faq: FaqEntry): void {
    this.editingFaq = faq;
    this.formData = { ...faq, tags: faq.tags ? [...faq.tags] : [] };
    this.showForm = true;
  }

  closeForm(): void {
    this.showForm = false;
    this.editingFaq = null;
    this.formData = this.getEmptyFaq();
  }

  saveFaq(): void {
    if (!this.formData.question?.trim() || !this.formData.answer?.trim()) {
      alert('Question and Answer are required!');
      return;
    }

    this.isLoading = true;

    if (this.editingFaq && this.editingFaq.id) {
      // Update existing FAQ
      this.faqService.updateFaq(this.editingFaq.id, this.formData).subscribe({
        next: () => {
          this.loadFaqs();
          this.closeForm();
        },
        error: (err) => {
          this.error = 'Failed to update FAQ.';
          this.isLoading = false;
          console.error('Error updating FAQ:', err);
        }
      });
    } else {
      // Create new FAQ
      this.faqService.createFaq(this.formData).subscribe({
        next: () => {
          this.loadFaqs();
          this.closeForm();
        },
        error: (err) => {
          this.error = 'Failed to create FAQ.';
          this.isLoading = false;
          console.error('Error creating FAQ:', err);
        }
      });
    }
  }

  deleteFaq(faq: FaqEntry): void {
    if (!faq.id) return;

    if (confirm(`Are you sure you want to delete this FAQ?\n\nQuestion: ${faq.question}`)) {
      this.isLoading = true;

      this.faqService.deleteFaq(faq.id).subscribe({
        next: () => {
          this.loadFaqs();
        },
        error: (err) => {
          this.error = 'Failed to delete FAQ.';
          this.isLoading = false;
          console.error('Error deleting FAQ:', err);
        }
      });
    }
  }

  toggleActive(faq: FaqEntry): void {
    if (!faq.id) return;

    const updatedFaq = { ...faq, isActive: !faq.isActive };

    this.faqService.updateFaq(faq.id, updatedFaq).subscribe({
      next: () => {
        this.loadFaqs();
      },
      error: (err) => {
        this.error = 'Failed to toggle FAQ status.';
        console.error('Error toggling FAQ:', err);
      }
    });
  }

  addTag(): void {
    const tagInput = prompt('Enter a new tag:');
    if (tagInput && tagInput.trim()) {
      if (!this.formData.tags) {
        this.formData.tags = [];
      }
      if (!this.formData.tags.includes(tagInput.trim())) {
        this.formData.tags.push(tagInput.trim());
      }
    }
  }

  removeTag(tag: string): void {
    if (this.formData.tags) {
      this.formData.tags = this.formData.tags.filter(t => t !== tag);
    }
  }

  private getEmptyFaq(): FaqEntry {
    return {
      question: '',
      answer: '',
      tags: [],
      isActive: true,
      displayOrder: 0
    };
  }
}
<div class="admin-dashboard">
  <div class="header">
    <h1>Admin Dashboard</h1>
  </div>

  <!-- Tab Navigation -->
  <div class="tabs">
    <button
      class="tab"
      [class.active]="activeTab === 'faqs'"
      (click)="switchTab('faqs')">
      📋 FAQ Management
    </button>
    <button
      class="tab"
      [class.active]="activeTab === 'documents'"
      (click)="switchTab('documents')">
      📄 Document Management
    </button>
    <button
      class="tab"
      [class.active]="activeTab === 'tenants'"
      (click)="switchTab('tenants')">
      🏢 Tenant Management
    </button>
    <button
      class="tab"
      [class.active]="activeTab === 'users'"
      (click)="switchTab('users')">
      👥 User Management
    </button>
  </div>

  <!-- FAQ Management Content -->
  <div *ngIf="activeTab === 'faqs'" class="tab-content">
    <div class="section-header">
      <h2>FAQ Management</h2>
      <button class="btn btn-primary" (click)="openCreateForm()">
        <span class="icon">+</span> Create New FAQ
      </button>
    </div>

  <!-- Error message -->
  <div *ngIf="error" class="error-message">
    {{ error }}
    <button class="close-btn" (click)="error = null">&times;</button>
  </div>

  <!-- Search and Filter Bar -->
  <div class="filter-bar">
    <div class="search-box">
      <input
        type="text"
        [(ngModel)]="searchQuery"
        (ngModelChange)="onSearchChange()"
        placeholder="Search questions, answers, or tags..."
        class="search-input"
      />
    </div>

    <div class="filter-group">
      <label>Status:</label>
      <select [(ngModel)]="filterActive" (ngModelChange)="onFilterChange()" class="filter-select">
        <option value="all">All</option>
        <option value="active">Active</option>
        <option value="inactive">Inactive</option>
      </select>
    </div>
  </div>

  <!-- Loading indicator -->
  <div *ngIf="isLoading" class="loading">Loading FAQs...</div>

  <!-- FAQ List -->
  <div class="faq-list" *ngIf="!isLoading">
    <div *ngIf="filteredFaqs.length === 0" class="empty-state">
      <p>No FAQs found. {{ searchQuery ? 'Try adjusting your search.' : 'Create your first FAQ to get started.' }}</p>
    </div>

    <div *ngFor="let faq of filteredFaqs" class="faq-card" [class.inactive]="!faq.isActive">
      <div class="faq-header">
        <div class="faq-status">
          <span class="status-badge" [class.active]="faq.isActive" [class.inactive]="!faq.isActive">
            {{ faq.isActive ? 'Active' : 'Inactive' }}
          </span>
          <span class="usage-count" title="Usage count">📊 {{ faq.usageCount || 0 }}</span>
        </div>
        <div class="faq-actions">
          <button class="btn-icon" (click)="toggleActive(faq)" title="Toggle active status">
            {{ faq.isActive ? '👁️' : '🚫' }}
          </button>
          <button class="btn-icon" (click)="openEditForm(faq)" title="Edit">✏️</button>
          <button class="btn-icon btn-delete" (click)="deleteFaq(faq)" title="Delete">🗑️</button>
        </div>
      </div>

      <div class="faq-content">
        <div class="question">
          <strong>Q:</strong> {{ faq.question }}
        </div>
        <div class="answer">
          <strong>A:</strong> {{ faq.answer }}
        </div>
      </div>

      <div class="faq-footer" *ngIf="faq.tags && faq.tags.length > 0">
        <div class="tags">
          <span *ngFor="let tag of faq.tags" class="tag">{{ tag }}</span>
        </div>
      </div>

      <div class="faq-meta">
        <small>Created: {{ faq.createdAt | date:'short' }}</small>
        <small *ngIf="faq.displayOrder !== undefined">Order: {{ faq.displayOrder }}</small>
      </div>
    </div>
  </div>

  <!-- Create/Edit Form Modal -->
  <div class="modal-overlay" *ngIf="showForm" (click)="closeForm()">
    <div class="modal-content" (click)="$event.stopPropagation()">
      <div class="modal-header">
        <h2>{{ editingFaq ? 'Edit FAQ' : 'Create New FAQ' }}</h2>
        <button class="close-btn" (click)="closeForm()">&times;</button>
      </div>

      <div class="modal-body">
        <div class="form-group">
          <label for="question">Question *</label>
          <textarea
            id="question"
            [(ngModel)]="formData.question"
            rows="3"
            placeholder="Enter the question..."
            class="form-control"
            maxlength="1000"
          ></textarea>
        </div>

        <div class="form-group">
          <label for="answer">Answer *</label>
          <textarea
            id="answer"
            [(ngModel)]="formData.answer"
            rows="6"
            placeholder="Enter the answer..."
            class="form-control"
          ></textarea>
        </div>

        <div class="form-group">
          <label>Tags</label>
          <div class="tags-container">
            <span *ngFor="let tag of formData.tags" class="tag editable">
              {{ tag }}
              <button class="tag-remove" (click)="removeTag(tag)">&times;</button>
            </span>
            <button class="btn-add-tag" (click)="addTag()">+ Add Tag</button>
          </div>
        </div>

        <div class="form-row">
          <div class="form-group">
            <label for="displayOrder">Display Order</label>
            <input
              id="displayOrder"
              type="number"
              [(ngModel)]="formData.displayOrder"
              class="form-control"
              min="0"
            />
          </div>

          <div class="form-group">
            <label class="checkbox-label">
              <input
                type="checkbox"
                [(ngModel)]="formData.isActive"
              />
              Active
            </label>
          </div>
        </div>
      </div>

      <div class="modal-footer">
        <button class="btn btn-secondary" (click)="closeForm()">Cancel</button>
        <button class="btn btn-primary" (click)="saveFaq()">
          {{ editingFaq ? 'Update' : 'Create' }} FAQ
        </button>
      </div>
    </div>
  </div>
  </div>

  <!-- Document Management Content -->
  <div *ngIf="activeTab === 'documents'" class="tab-content">
    <app-document-management></app-document-management>
  </div>
</div>
