import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { TenantService, TenantDto, CreateTenantRequest } from '../../services/tenant.service';
import { WidgetCustomizerComponent } from '../widget-customizer/widget-customizer.component';

@Component({
  selector: 'app-tenant-management',
  standalone: true,
  imports: [CommonModule, FormsModule, WidgetCustomizerComponent],
  templateUrl: './tenant-management.component.html',
  styleUrls: ['./tenant-management.component.css']
})
export class TenantManagementComponent implements OnInit {
  tenants: TenantDto[] = [];
  loading = false;
  error: string | null = null;
  showCreateForm = false;
  showWidgetCustomizer = false;
  editingTenant: TenantDto | null = null;
  customizingTenant: TenantDto | null = null;

  newTenant: CreateTenantRequest = {
    name: '',
    contactEmail: '',
    domain: '',
    maxUsers: 10,
    maxDocuments: 100
  };

  constructor(private tenantService: TenantService) {}

  ngOnInit() {
    this.loadTenants();
  }

  loadTenants() {
    this.loading = true;
    this.error = null;

    this.tenantService.getAllTenants().subscribe({
      next: (data) => {
        this.tenants = data;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Fehler beim Laden der Tenants: ' + err.message;
        this.loading = false;
      }
    });
  }

  openCreateForm() {
    this.showCreateForm = true;
    this.editingTenant = null;
    this.resetForm();
  }

  openEditForm(tenant: TenantDto) {
    this.editingTenant = tenant;
    this.showCreateForm = true;
    this.newTenant = {
      name: tenant.name,
      contactEmail: tenant.contactEmail,
      domain: tenant.domain || '',
      maxUsers: tenant.maxUsers,
      maxDocuments: tenant.maxDocuments,
      settings: tenant.settings
    };
  }

  closeForm() {
    this.showCreateForm = false;
    this.editingTenant = null;
    this.resetForm();
  }

  resetForm() {
    this.newTenant = {
      name: '',
      contactEmail: '',
      domain: '',
      maxUsers: 10,
      maxDocuments: 100
    };
  }

  saveTenant() {
    if (!this.newTenant.name || !this.newTenant.contactEmail) {
      this.error = 'Name und E-Mail sind erforderlich';
      return;
    }

    this.loading = true;
    this.error = null;

    const request = this.editingTenant
      ? this.tenantService.updateTenant(this.editingTenant.id, this.newTenant)
      : this.tenantService.createTenant(this.newTenant);

    request.subscribe({
      next: () => {
        this.loadTenants();
        this.closeForm();
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Fehler beim Speichern: ' + err.error?.message || err.message;
        this.loading = false;
      }
    });
  }

  toggleStatus(tenant: TenantDto) {
    if (!confirm(`${tenant.name} ${tenant.isActive ? 'deaktivieren' : 'aktivieren'}?`)) {
      return;
    }

    this.tenantService.toggleTenantStatus(tenant.id).subscribe({
      next: () => this.loadTenants(),
      error: (err) => {
        this.error = 'Fehler beim Ã„ndern des Status: ' + err.message;
      }
    });
  }

  regenerateApiKey(tenant: TenantDto) {
    if (!confirm(`API-Key fÃ¼r ${tenant.name} neu generieren? Der alte Key wird ungÃ¼ltig!`)) {
      return;
    }

    this.tenantService.regenerateApiKey(tenant.id).subscribe({
      next: () => this.loadTenants(),
      error: (err) => {
        this.error = 'Fehler beim Generieren des API-Keys: ' + err.message;
      }
    });
  }

  deleteTenant(tenant: TenantDto) {
    if (!confirm(`${tenant.name} wirklich lÃ¶schen? Dies kann nicht rÃ¼ckgÃ¤ngig gemacht werden!`)) {
      return;
    }

    this.tenantService.deleteTenant(tenant.id).subscribe({
      next: () => this.loadTenants(),
      error: (err) => {
        this.error = 'Fehler beim LÃ¶schen: ' + err.error?.message || err.message;
      }
    });
  }

  copyToClipboard(text: string) {
    navigator.clipboard.writeText(text).then(() => {
      alert('In Zwischenablage kopiert!');
    });
  }

  openWidgetCustomizer(tenant: TenantDto) {
    this.customizingTenant = tenant;
    this.showWidgetCustomizer = true;
  }

  closeWidgetCustomizer() {
    this.showWidgetCustomizer = false;
    this.customizingTenant = null;
  }
}
<div class="tenant-management">
  <div class="header">
    <h2>ğŸ¢ Tenant Verwaltung</h2>
    <button class="btn btn-primary" (click)="openCreateForm()" [disabled]="loading">
      â• Neuer Tenant
    </button>
  </div>

  <div class="error" *ngIf="error">{{ error }}</div>

  <div class="loading" *ngIf="loading && !showCreateForm">LÃ¤dt...</div>

  <!-- Create/Edit Form -->
  <div class="modal" *ngIf="showCreateForm">
    <div class="modal-content">
      <div class="modal-header">
        <h3>{{ editingTenant ? 'Tenant bearbeiten' : 'Neuen Tenant erstellen' }}</h3>
        <button class="close-btn" (click)="closeForm()">âœ•</button>
      </div>

      <div class="form-group">
        <label>Name *</label>
        <input type="text" [(ngModel)]="newTenant.name" placeholder="Firmenname" class="form-control" />
      </div>

      <div class="form-group">
        <label>Kontakt E-Mail *</label>
        <input type="email" [(ngModel)]="newTenant.contactEmail" placeholder="kontakt@firma.de" class="form-control" />
      </div>

      <div class="form-group">
        <label>Domain (optional)</label>
        <input type="text" [(ngModel)]="newTenant.domain" placeholder="www.firma.de" class="form-control" />
      </div>

      <div class="form-row">
        <div class="form-group">
          <label>Max. Benutzer</label>
          <input type="number" [(ngModel)]="newTenant.maxUsers" min="1" class="form-control" />
        </div>

        <div class="form-group">
          <label>Max. Dokumente</label>
          <input type="number" [(ngModel)]="newTenant.maxDocuments" min="1" class="form-control" />
        </div>
      </div>

      <div class="modal-actions">
        <button class="btn btn-secondary" (click)="closeForm()">Abbrechen</button>
        <button class="btn btn-primary" (click)="saveTenant()" [disabled]="loading">
          {{ editingTenant ? 'Speichern' : 'Erstellen' }}
        </button>
      </div>
    </div>
  </div>

  <!-- Tenants List -->
  <div class="tenants-list" *ngIf="!loading || showCreateForm">
    <div class="tenant-card" *ngFor="let tenant of tenants" [class.inactive]="!tenant.isActive">
      <div class="tenant-header">
        <div class="tenant-info">
          <h3>{{ tenant.name }}</h3>
          <span class="tenant-id">ID: {{ tenant.tenantId }}</span>
          <span class="status-badge" [class.active]="tenant.isActive" [class.inactive]="!tenant.isActive">
            {{ tenant.isActive ? 'âœ“ Aktiv' : 'âœ• Inaktiv' }}
          </span>
        </div>
        <div class="tenant-actions">
          <button class="btn btn-sm btn-primary" (click)="openEditForm(tenant)">âœï¸ Bearbeiten</button>
          <button class="btn btn-sm" [class.btn-warning]="tenant.isActive" [class.btn-success]="!tenant.isActive"
                  (click)="toggleStatus(tenant)">
            {{ tenant.isActive ? 'ğŸš« Deaktivieren' : 'âœ“ Aktivieren' }}
          </button>
          <button class="btn btn-sm btn-danger" (click)="deleteTenant(tenant)"
                  *ngIf="tenant.tenantId !== 'system'">
            ğŸ—‘ï¸ LÃ¶schen
          </button>
        </div>
      </div>

      <div class="tenant-details">
        <div class="detail-item">
          <strong>E-Mail:</strong> {{ tenant.contactEmail }}
        </div>
        <div class="detail-item" *ngIf="tenant.domain">
          <strong>Domain:</strong> {{ tenant.domain }}
        </div>
        <div class="detail-item">
          <strong>Benutzer:</strong> {{ tenant.currentUserCount || 0 }} / {{ tenant.maxUsers }}
        </div>
        <div class="detail-item">
          <strong>Dokumente:</strong> {{ tenant.currentDocumentCount || 0 }} / {{ tenant.maxDocuments }}
        </div>
        <div class="detail-item">
          <strong>Erstellt:</strong> {{ tenant.createdAt | date: 'dd.MM.yyyy HH:mm' }}
        </div>
      </div>

      <div class="api-key-section">
        <label><strong>API Key:</strong></label>
        <div class="api-key-row">
          <input type="text" [value]="tenant.apiKey" readonly class="api-key-input" />
          <button class="btn btn-sm btn-secondary" (click)="copyToClipboard(tenant.apiKey!)">
            ğŸ“‹ Kopieren
          </button>
          <button class="btn btn-sm btn-warning" (click)="regenerateApiKey(tenant)">
            ğŸ”„ Neu generieren
          </button>
        </div>
      </div>

      <div class="widget-config-section">
        <button class="btn btn-primary btn-widget-config" (click)="openWidgetCustomizer(tenant)">
          ğŸ¨ Widget konfigurieren & Embed-Code generieren
        </button>
      </div>
    </div>

    <div class="empty-state" *ngIf="tenants.length === 0">
      <p>Keine Tenants vorhanden. Erstellen Sie den ersten Tenant!</p>
    </div>
  </div>

  <!-- Widget Customizer Modal -->
  <div class="modal modal-large" *ngIf="showWidgetCustomizer && customizingTenant">
    <div class="modal-content modal-content-large">
      <div class="modal-header">
        <h3>ğŸ¨ Widget-Konfiguration fÃ¼r {{ customizingTenant.name }}</h3>
        <button class="close-btn" (click)="closeWidgetCustomizer()">âœ•</button>
      </div>
      <div class="modal-body">
        <app-widget-customizer [tenant]="customizingTenant"></app-widget-customizer>
      </div>
    </div>
  </div>
</div>
